DROP TABLE IF EXISTS `guanchazhe`;
CREATE TABLE `guanchazhe` (
  `Id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(255) DEFAULT NULL,
  `author` varchar(255) DEFAULT NULL,
